"use strict";(()=>{var r=document.querySelector(".swiper"),t=document.querySelectorAll(".swiper-slide").length;$(".swiper-number-total").text(t);var s=new Swiper(r,{slidesPerView:2,keyboard:{enabled:!0},slidesPerGroup:2,spaceBetween:0,breakpoints:{320:{slidesPerView:1,slidesPerGroup:1},768:{slidesPerView:2}},navigation:{nextEl:document.querySelector(".swiper-next"),prevEl:document.querySelector(".swiper-prev")},zoom:{maxRatio:1.5}});s.on("slideChange",function(e){$(".swiper-number-current").text(e.realIndex+1)});})();
